import { render, screen } from "@testing-library/react";

import Image from "@psi/styleguide/components/Image";

test("Image renders", () => {
  render(
    <Image
      src="data:image/jpg;base64,"
      label="example image"
    />,
  );
  const image = screen.getByAltText("example image");

  expect(image).toBeInTheDocument();
});
